            document.getElementById("colonia").value = document.getElementById("colonia").value.trim();  
            document.getElementById("ocupacion").value = document.getElementById("ocupacion").value.trim();  
            document.getElementById("nacionalidad").value = document.getElementById("nacionalidad").value.trim();
            document.getElementById("cadena1").value = document.getElementById("cadena1").value.trim(); 
            document.getElementById("descripcion1").value = document.getElementById("descripcion1").value.trim(); 
            
            document.getElementById("cadena2").value = document.getElementById("cadena2").value.trim(); 
            document.getElementById("descripcion2").value = document.getElementById("descripcion2").value.trim();  

            document.getElementById("cadena3").value = document.getElementById("cadena3").value.trim(); 
            document.getElementById("descripcion3").value = document.getElementById("descripcion3").value.trim();  

            document.getElementById("cadena4").value = document.getElementById("cadena4").value.trim(); 
            document.getElementById("descripcion4").value = document.getElementById("descripcion4").value.trim();  
  
            if (formulario.checkValidity()){

                const archivoIdentificacion = document.getElementById('identificacion');
                const archivoComprobante = document.getElementById('comprobante');
                const archivoManifiesto = document.getElementById('manifiesto');

                const fileIdentificacion = archivoIdentificacion.files[0];
                const fileComprobante = archivoComprobante.files[0];
                const fileManifiesto = archivoManifiesto.files[0]; 

                const archivoImg1 = document.getElementById('imagen1');
                const archivoImg2 = document.getElementById('imagen2');
                const archivoImg3 = document.getElementById('imagen3');
                const archivoImg4 = document.getElementById('imagen4');

                const fileImg1 = archivoImg1.files[0];
                const fileImg2 = archivoImg2.files[0];
                const fileImg3 = archivoImg3.files[0];
                const fileImg4 = archivoImg4.files[0];


                const maxSizePdf = 1024 * 1024 * 5; // 5MB
                const maxSizeImg = 1024 * 1024 * 5; // 5MB
                    
                    if(fileImg1){
                        if (fileImg1.size > maxSizeImg) {
                            alert("El archivo de la imagen 1 en la categoría: Producto innovador. Excede el tamaño máximo permitido de 5MB.");
                            archivoImg1.value = '';
                            validaArchivos++;
                        }
                    }
                    

                    if(fileImg2){
                        if (fileImg2.size > maxSizeImg) {
                            alert("El archivo de la imagen 2 en la categoría: Producto innovador. Excede el tamaño máximo permitido de 5MB.");
                            archivoImg2.value = '';
                            validaArchivos++;
                        }
                    }

                    if(fileImg3){
                        if (fileImg3.size > maxSizeImg) {
                            alert("El archivo de la imagen 1 en la categoría: Interacción Ciber-Humana. Excede el tamaño máximo permitido de 5MB.");
                            archivoImg3.value = '';
                            validaArchivos++;
                        }
                    }

                    if(fileImg4){
                        if (fileImg4.size > maxSizeImg) {
                            alert("El archivo de la imagen 2 en la categoría: Producto innovador. Excede el tamaño máximo permitido de 5MB.");
                            archivoImg4.value = '';
                            validaArchivos++;
                        }
                    }

                    if (fileIdentificacion.size > maxSizePdf) {
                        alert("El archivo de identificación excede el tamaño máximo permitido de 5MB.");
                        archivoIdentificacion.value = ''; 
                        validaArchivos++;
                    }

                    if (fileComprobante.size > maxSizePdf) {
                        alert("El archivo de comprobante excede el tamaño máximo permitido de 5MB.");
                        archivoComprobante.value = ''; 
                        validaArchivos++;
                    }

                    if (fileManifiesto.size > maxSizePdf) {
                        alert("El archivo de manifiesto excede el tamaño máximo permitido de 5MB.");
                        archivoManifiesto.value = ''; // Limpiar el valor del input para permitir volver a seleccionar el archivo
                        validaArchivos++;
                    }
                    
                    if(validaArchivos == 0){
                        document.getElementById("form-registro").submit();
                    }

                //
            }else{
                //alert("Debes llenar los campos correspondientes");
                formulario.reportValidity();
    
                var campos = formulario.elements;
                
                for (var i = 0; i < campos.length; i++) {
                   
                    if (!campos[i].validity.valid) {
                        campos[i].focus();
                        break;
                    }
                }
            }              
        }
    }
</script>

